<style>
	nav {
		border-bottom: 1px solid hsl(349, 100%, 16%);
		border-top: 1px solid hsl(349, 100%, 16%);
		font-weight: 300;
	}

	ul {
		display: flex;
		justify-content: end;
		justify-content: flex-end;
		margin: 0;
		margin-right: 1em;
		padding: 0;
	}

	li {
		list-style: none;
		position: relative;
		cursor: pointer;
	}

	a:active {
		color: var(--accent);
	}

	.active {
		color: var(--accent);
	}

	.active::after {
		position: absolute;
		content: '';
		width: calc(100% - 1em);
		height: 4px;
		background-color: var(--accent);
		border-radius: 20px;
		display: block;
		bottom: -1px;
		transition: ease-in-out 400ms;
	}

	.activeIcon {
		fill: var(--accent);
	}

	a {
		text-decoration: none;
		padding: 1em 0.5em;
		display: block;
	}

	svg {
		display: none;
		position: absolute;
		top: -14%;
		left: calc(50% - 12px);
		height: 1.7em;
		fill: var(--soft-white)
	}

	@media (max-width: 480px) {
		nav {
			position: fixed;
			bottom: 0;
			right:0;
			left: 0;
			padding: 0.8em 0em 0 0;
			border-top-width: 3px;
			border-bottom: none;
			background-color: var(--darker-blue);
			z-index: 20;
		}
		ul {
			justify-content: center;
		}

		li {
			transform: translateY(5px);
		}

		li a::before {
			content: '';
			position: absolute;
			height: 1.2rem;
			width: 1.2rem;
			top: -10%;
			left: 40%;
		}

		svg {
			display: block;
		}

		.active::after {
			display: none;
		}
	}
</style>


